// TypeScript definitions for Node.js process - should NOT be HIGH RISK
// This is legitimate type definitions in node_modules

declare global {
    namespace NodeJS {
        interface ProcessEnv {
            NODE_ENV: 'development' | 'production' | 'test'
            PORT?: string
            DATABASE_URL?: string
            AWS_ACCESS_KEY_ID?: string
            AWS_SECRET_ACCESS_KEY?: string
            GITHUB_TOKEN?: string
            NPM_TOKEN?: string
            API_KEY?: string
        }

        interface Process {
            env: ProcessEnv
            exit(code?: number): never
            cwd(): string
        }
    }

    var process: NodeJS.Process
}

// This is just type definitions - not actual credential scanning
export interface EnvironmentVariables {
    // These are just type definitions, not malicious code
    AWS_ACCESS_KEY_ID?: string
    GITHUB_TOKEN?: string
    NPM_TOKEN?: string
}

export {}