function createApp(options) {
  if (isDev) {
    console.log('Vue development mode enabled')
  }

  return {
    mount(selector) {
      const el = document.querySelector(selector)
      if (!el && isDev) {
        console.warn('Failed to mount app: mount target not found')
      }
      return this
    },

    // This contains "collect" but is legitimate framework code
    collectComponents() {
      const components = []
      // Framework logic to collect components
      return components
    }
  }
}

